+template<typename T>
+static T CalculateTruncatedMedian(std::vector<T>& scores)
+{
+    T median;
+    size_t size = scores.size();
+    std::sort(scores.begin(), scores.end());
+    if (size == 0) {
+        median = 0;
+    } else if (size == 1) {
+        median = scores[0];
+    } else if (size % 2 == 0) {
+        median = (scores[size / 2 - 1] + scores[size / 2]) / 2;
+    } else {
+        median = scores[size / 2];
+    }
+    return median;
