+            map_stats[plot_value].push_back(maxfee);
+        } else if (plot_value == "minfeerate") {
+            map_stats[plot_value].push_back((minfeerate == MAX_MONEY) ? 0 : minfeerate);
+        } else if (plot_value == "minfeerate_old") {
+            map_stats[plot_value].push_back((minfeerate_old == MAX_MONEY) ? 0 : minfeerate_old);
+        } else if (plot_value == "maxfeerate") {
+            map_stats[plot_value].push_back(maxfeerate);
+        } else if (plot_value == "maxfeerate_old") {
+            map_stats[plot_value].push_back(maxfeerate_old);
+        } else if (plot_value == "avgfeerate") {
+            map_stats[plot_value].push_back(CFeeRate(totalfee, total_weight).GetTruncatedFee(WITNESS_SCALE_FACTOR));
