+static std::map<std::string, UniValue> GetBlockStatsMap(const CBlock& block, const CBlockIndex* pindex, const std::set<std::string>& stats)
+{
+    std::map<std::string, UniValue> map_stats;
+    int64_t inputs = 0;
+    int64_t outputs = 0;
+    int64_t swtxs = 0;
+    int64_t total_size = 0;
+    int64_t total_weight = 0;
+    int64_t swtotal_size = 0;
+    int64_t swtotal_weight = 0;
+    int64_t utxo_size_inc = 0;
+    CAmount total_out = 0;
+    CAmount totalfee = 0;
+    CAmount minfee = MAX_MONEY;
+    CAmount maxfee = 0;
+    CAmount minfeerate = MAX_MONEY;
+    CAmount maxfeerate = 0;
+    std::vector<CAmount> fee_array;
+    std::vector<CAmount> feerate_array;
+    int64_t mintxsize = MAX_BLOCK_SERIALIZED_SIZE;
+    int64_t maxtxsize = 0;
+    std::vector<int64_t> txsize_array;
+
+    const bool do_mediantxsize = stats.count("mediantxsize") != 0;
+    const bool do_medianfee = stats.count("medianfee") != 0;
+    const bool do_medianfeerate = stats.count("medianfeerate") != 0;
+
+    const bool loop_outputs = stats.count("total_out") != 0 || stats.count("utxo_size_inc") != 0 ||
+        stats.count("totalfee") != 0 || stats.count("avgfee") != 0 || stats.count("avgfeerate") != 0 ||
+        do_medianfee || stats.count("minfee") != 0 || stats.count("maxfee") != 0 ||
+        do_medianfeerate || stats.count("minfeerate") != 0 || stats.count("maxfeerate") != 0;
+
+    const bool do_calculate_size = do_mediantxsize || stats.count("total_size") != 0 || stats.count("avgtxsize") != 0 ||
+        stats.count("mintxsize") != 0 || stats.count("maxtxsize") != 0 || stats.count("swtotal_size") != 0;
+
+    const bool do_calculate_weight = stats.count("total_weight") != 0 || stats.count("avgfeerate") != 0 ||
+        stats.count("swtotal_weight") != 0 || stats.count("medianfeerate_weight") != 0;
+
+    const bool do_calculate_sw = stats.count("swtxs") != 0 || stats.count("swtotal_size") != 0 ||
+        stats.count("swtotal_weight") != 0;
+
+    const bool loop_inputs = stats.count("totalfee") != 0 || stats.count("avgfee") != 0 || stats.count("avgfeerate") != 0 ||
+        do_medianfee || stats.count("minfee") != 0 || stats.count("maxfee") != 0 ||
+        do_medianfeerate || stats.count("minfeerate") != 0 || stats.count("maxfeerate") != 0;
+
+    for (const auto& tx : block.vtx) {
+
+        outputs += tx->vout.size();
+
+        CAmount tx_total_out = 0;
+        if (loop_outputs) {
