+            CAmount feerate = CFeeRate(txfee, weight).GetTruncatedFee(WITNESS_SCALE_FACTOR);
+            if (do_medianfeerate) {
+                feerate_array.push_back(feerate);
+            }
+            minfeerate = std::min(minfeerate, feerate);
+            maxfeerate = std::max(maxfeerate, feerate);
+        }
+    }
+
+    map_stats["height"] = (int64_t)pindex->nHeight;
+    map_stats["time"] = pindex->GetBlockTime();
+    map_stats["mediantime"] = pindex->GetMedianTimePast();
+    map_stats["subsidy"] = GetBlockSubsidy(pindex->nHeight, Params().GetConsensus());
+    map_stats["totalfee"] = totalfee;
+    map_stats["txs"] = (int64_t)block.vtx.size();
+    map_stats["swtxs"] = swtxs;
+    map_stats["ins"] = inputs;
+    map_stats["outs"] = outputs;
+    map_stats["utxo_increase"] = outputs - inputs;
+    map_stats["utxo_size_inc"] = utxo_size_inc;
+    map_stats["total_size"] = total_size;
+    map_stats["total_weight"] = total_weight;
+    map_stats["swtotal_size"] = swtotal_size;
+    map_stats["swtotal_weight"] = swtotal_weight;
+    map_stats["total_out"] = total_out;
+    map_stats["minfee"] = (minfee == MAX_MONEY) ? 0 : minfee;
+    map_stats["maxfee"] = maxfee;
+    map_stats["medianfee"] = CalculateTruncatedMedian(fee_array);
+    map_stats["avgfee"] = (block.vtx.size() > 1) ? totalfee / (block.vtx.size() - 1) : 0;
+    map_stats["minfeerate"] = (minfeerate == MAX_MONEY) ? 0 : minfeerate;
+    map_stats["maxfeerate"] = maxfeerate;
+    map_stats["medianfeerate"] = CalculateTruncatedMedian(feerate_array);
+    map_stats["avgfeerate"] = CFeeRate(totalfee, total_weight).GetTruncatedFee(WITNESS_SCALE_FACTOR);
+    map_stats["mintxsize"] = mintxsize == MAX_BLOCK_SERIALIZED_SIZE ? 0 : mintxsize;
+    map_stats["maxtxsize"] = maxtxsize;
+    map_stats["mediantxsize"] = CalculateTruncatedMedian(txsize_array);
+    map_stats["avgtxsize"] = (block.vtx.size() > 1) ? total_size / (block.vtx.size() - 1) : 0;
+
+    return map_stats;
+}
