+
+#include <type_traits>
+#include <cstddef>
+
+/** A Slice is a pointer to a non-owned continuous array that acts like a container. */
+template<typename C>
+class Slice
+{
+    C* m_data;
+    std::size_t m_size;
+
+public:
+    Slice() : m_data(nullptr), m_size(0) {}
+    Slice(C* data, std::size_t size) : m_data(data), m_size(size) {}
+
+    C* data() const { return m_data; }
+    std::size_t size() const { return m_size; }
+
+    C* begin() const { return m_data; }
+    C* end() const { return m_data + m_size; }
+
+    C& operator[](std::size_t pos) const { return m_data[pos]; }
+};
+
+/** Create a slice to a container exposing data() and size()
+ *
+ * This correctly deals with constness: if either the element type is const
+ * or a reference to a const container is passed, the result will be a const
+ * Slice. */
+template<typename V>
+Slice<typename std::remove_pointer<decltype(std::declval<V>().data())>::type> MakeSlice(V& v) { return Slice<typename std::remove_pointer<decltype(std::declval<V>().data())>::type>(v.data(), v.size()); }

