         double progress_current = progress_begin;
+        int n = 5;
         while (pindex && !fAbortRescan && !ShutdownRequested()) {
+            MilliSleep(1000);
+            if (n-- == 0) fAbortRescan = true;
             if (pindex->nHeight % 100 == 0 && progress_end - progress_begin > 0.0) {
