+void PSBTOperationsDialog::openWithPSBT(PartiallySignedTransaction psbtx)
+{
+    m_transaction_data = psbtx;
+
+    bool complete;
+    TransactionError err = m_wallet_model->wallet().fillPSBT(m_transaction_data, complete, SIGHASH_ALL, false /* sign */, true /* bip32derivs */);
+    if (err != TransactionError::OK) {
+        showStatus(tr("Failed to load transaction: %1")
+            .arg(QString::fromStdString(TransactionErrorString(err))), StatusLevel::ERROR);
+        return;
+    }
+
+    m_ui->broadcastTransactionButton->setEnabled(complete);
+    m_ui->signTransactionButton->setEnabled(!complete);
+
+    updateTransactionDisplay();
