+
+void PSBTOperationsDialog::signTransaction()
+{
+    size_t unsigned_count = CountPSBTUnsignedInputs(m_transaction_data);
+
+    bool complete;
+    TransactionError err = m_wallet_model->wallet().fillPSBT(m_transaction_data, complete, SIGHASH_ALL, true /* sign */, true /* bip32derivs */);
+    size_t did_sign_count = unsigned_count - CountPSBTUnsignedInputs(m_transaction_data);
+
+    if (err != TransactionError::OK) {
+        showStatus(tr("Failed to sign transaction: %1")
+            .arg(QString::fromStdString(TransactionErrorString(err))), StatusLevel::ERROR);
+        return;
+    }
+
+    updateTransactionDisplay();
+
+    if (!complete && did_sign_count < 1) {
+        showStatus(tr("Could not sign any more inputs."), StatusLevel::WARNING);
+    } else if (!complete) {
+        showStatus(tr("Signed transaction successfully, but more signatures are still required."),
+            StatusLevel::INFO);
+    } else {
+        showStatus(tr("Signed transaction sucessfully. Transaction is ready to broadcast."),
+            StatusLevel::INFO);
+        m_ui->broadcastTransactionButton->setEnabled(true);
+    }
+}
+
+void PSBTOperationsDialog::broadcastTransaction()
+{
+    CMutableTransaction mtx;
+    if (!FinalizeAndExtractPSBT(m_transaction_data, mtx)) {
+        // This is never expected to fail unless we were given a malformed PSBT
+        // (e.g. with an invalid signature.)
+        showStatus(tr("Unknown error processing transaction."), StatusLevel::ERROR);
+        return;
+    }
+
+    CTransactionRef tx = MakeTransactionRef(mtx);
+    std::string err_string;
+    TransactionError error = BroadcastTransaction(
+        *m_client_model->node().context(), tx, err_string, COIN / 10, /* relay */ true, /* await_callback */ false);
+
+    if (error == TransactionError::OK) {
+        showStatus(tr("Transaction broadcast successfully! Transaction ID: %1")
+            .arg(QString::fromStdString(tx->GetHash().GetHex())), StatusLevel::INFO);
+    } else {
+        showStatus(tr("Transaction broadcast failed: %1")
+            .arg(QString::fromStdString(TransactionErrorString(error))), StatusLevel::ERROR);
+    }
+}
