+// Represents a non-owned SOCKET.
+//
+// The underlying SOCKET will NOT be closed automatically on object destruction.
+class Socket : public MockableSocket
+{
+    SOCKET m_socket = INVALID_SOCKET;
+
+public:
+    explicit Socket(SOCKET socket) : m_socket{socket}
+    {
+    }
+
+    // Send bytes on the socket. See send(2).
+    ssize_t send(const char* buf, size_t len, int flags) override
+    {
+        return ::send(m_socket, buf, len, flags);
+    }
+
+    // Receive bytes from the socket. See recv(2).
+    ssize_t recv(char* buf, size_t len, int flags) override
+    {
+        return ::recv(m_socket, buf, len, flags);
+    }
