                            case ConnectionType::FEELER:
                            case ConnectionType::ADDR_FETCH:
                                break;
                            case ConnectionType::OUTBOUND:
                            case ConnectionType::BLOCK_RELAY:
                                if (pnode->fSuccessfullyConnected) {
                                    ++nRelevant;
                                }   
                                break;
                            }
```

