-        if (fmt == AddrSerialization::DISK) {
+        switch (fmt) {
+        case AddrSerialization::DISK: {
             uint32_t disk_version = DISK_VERSION;
             READWRITE(disk_version);
             READWRITE(obj.nTime);
-        } else if (fmt == AddrSerialization::NETWORK_WITHTIME) {
+            break;
+        }
+        case AddrSerialization::NETWORK_WITHTIME: {
