+                    }
+                    dests_to_delete.push_back(addr_pair.first);
+                    continue;
+                }
+            }
+            // Not ours, not in watchonly wallet, and not in solvable
+            error = _("Error: Address book data in wallet cannot be identified to belong to migrated wallets");
