This is probably useful for the Win64 test failure:

```cpp
// Run with: x86_64-w64-mingw32-g++ --std=c++17 --static paths.cpp17.cpp && wine ./a.exe
// For comparison: g++ --std=c++17 --static paths.cpp17.cpp && ./a.out

#include <string>
#include <iostream>
#include <filesystem>
namespace fs = std::filesystem;

int main()
{
    // Basically copied from init_test_fixture.cpp:
    fs::path m_cwd = fs::current_path();

    std::string sep;
    sep += fs::path::preferred_separator;

    fs::path m_datadir = fs::current_path();

    fs::path default_path = m_cwd / "wallets";
    fs::path custom = m_datadir / "my_wallets";
    fs::path nonexistent = m_datadir / "path_does_not_exist";
    fs::path file = m_datadir / "not_a_directory.dat";
    fs::path trailing = m_datadir / "wallets" / sep;

    // *sep* is "appended" and not "concatenated".
    // https://en.cppreference.com/w/cpp/filesystem/path/append
    // https://en.cppreference.com/w/cpp/filesystem/path/concat
    fs::path trailing2 = m_datadir / "wallets" / sep / sep;

    std::cout << "default path: " << default_path << "\n";
    std::cout << "custom: " << custom << "\n";
    std::cout << "nonexistent: " << nonexistent << "\n";
    std::cout << "file: " << file << "\n";
    std::cout << "trailing: " << trailing << "\n";
    std::cout << "trailing2: " << trailing2 << "\n";

    // Demonstrates "+=" behavior
    fs::path trailing_plus = m_datadir / "wallets";
    trailing_plus += sep;

    std::cout << "trailing_plus: " << trailing_plus << "\n";
    std::cout << "absolute(trailing_plus): " << fs::absolute(trailing_plus) << "\n";
    std::cout << "canonical(trailing_plus): " << fs::canonical(trailing_plus) << "\n"; // Directory must exist! Otherwise an exception is thrown.

    // See "normalize algorithm" here: https://en.cppreference.com/w/cpp/filesystem/path 
    std::cout << "trailing_plus.lexically_normal(): " << trailing_plus.lexically_normal() << "\n";

    std::cout << "end\n"; // breakpoint line :)
}
```

