-    // index entries rely on assumed-valid ancestors.
-    bool seen_assumed_valid{false};
+    // Find start of assumed-valid region.
+    int first_assumed_valid_height = std::numeric_limits<int>::max();
+    for (const auto& [height, block] : vSortedByHeight) {
+        if (block->IsAssumedValid()) {
+            first_assumed_valid_height = height;
+            break;
+        }
+    }
 
     for (const std::pair<int, CBlockIndex*>& item : vSortedByHeight)
     {
@@ -3634,10 +3639,6 @@ bool BlockManager::LoadBlockIndex(
         pindex->nChainWork = (pindex->pprev ? pindex->pprev->nChainWork : 0) + GetBlockProof(*pindex);
         pindex->nTimeMax = (pindex->pprev ? std::max(pindex->pprev->nTimeMax, pindex->nTime) : pindex->nTime);
 
-        if (!seen_assumed_valid && pindex->IsAssumedValid()) {
-            seen_assumed_valid = true;
-        }
-
         // We can link the chain of blocks for which we've received transactions at some point, or
         // blocks that are assumed-valid on the basis of snapshot load (see
         // PopulateAndValidateSnapshot()).
@@ -3674,7 +3675,7 @@ bool BlockManager::LoadBlockIndex(
             // happen because assumed-valid blocks are buried deeply in
             // the valid chain.
             for (CChainState* chainstate : chainman.GetAll()) {
-                if (!seen_assumed_valid || chainstate->reliesOnAssumedValid()) {
+                if (chainstate->reliesOnAssumedValid() || pindex->nHeight < first_assumed_valid_height) {
                     chainstate->setBlockIndexCandidates.insert(pindex);
